import React, { useState, useEffect } from 'react';
import { getKeyItemsList, getSpecificKeyItemAlerts } from '../services/api';
import { ChevronDown, ChevronUp, AlertTriangle, Package, Mail } from 'lucide-react';

const Dashboard = () => {
  const [keyItems, setKeyItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [expandedItems, setExpandedItems] = useState({});
  const [alerts, setAlerts] = useState({});
  const [loadingAlerts, setLoadingAlerts] = useState({});

  // Load key items on component mount
  useEffect(() => {
    loadKeyItems();
  }, []);

  const loadKeyItems = async () => {
    try {
      setLoading(true);
      const data = await getKeyItemsList();
      setKeyItems(data);

      // Auto-load alerts for all items that have low stock
      const itemsWithAlerts = data.filter(item => item.low_stock_count > 0);
      for (const item of itemsWithAlerts) {
        await loadAlertsForItem(item.name);
      }
    } catch (error) {
      console.error('Error loading key items:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadAlertsForItem = async (itemName) => {
    if (alerts[itemName]) return; // Already loaded

    try {
      setLoadingAlerts(prev => ({ ...prev, [itemName]: true }));
      const data = await getSpecificKeyItemAlerts(itemName);
      setAlerts(prev => ({ ...prev, [itemName]: data }));
    } catch (error) {
      console.error(`Error loading alerts for ${itemName}:`, error);
    } finally {
      setLoadingAlerts(prev => ({ ...prev, [itemName]: false }));
    }
  };

  const toggleExpanded = async (itemName) => {
    const isExpanding = !expandedItems[itemName];

    setExpandedItems(prev => ({
      ...prev,
      [itemName]: isExpanding
    }));

    // Load alerts when expanding for the first time
    if (isExpanding && !alerts[itemName]) {
      await loadAlertsForItem(itemName);
    }
  };

  const getTotalAlerts = () => {
    if (!Array.isArray(keyItems)) return 0;
    return keyItems.reduce((total, item) => total + (item.low_stock_count || 0), 0);
  };

  const getUrgencyLevel = (shortage) => {
    if (shortage >= 10) return { color: 'text-red-600', bg: 'bg-red-50', label: 'CRITICAL' };
    if (shortage >= 5) return { color: 'text-orange-600', bg: 'bg-orange-50', label: 'HIGH' };
    return { color: 'text-yellow-600', bg: 'bg-yellow-50', label: 'MEDIUM' };
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto p-6">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
          <span className="ml-3 text-lg text-gray-600">Loading inventory alerts...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-lg p-6 mb-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <Package className="w-8 h-8 text-blue-600 mr-3" />
            <div>
              <h1 className="text-3xl font-bold text-gray-800">Key Items Dashboard</h1>
              <p className="text-gray-600">Monitor low stock alerts for critical inventory items</p>
            </div>
          </div>
          <div className="text-right">
            <div className="text-2xl font-bold text-red-600">{getTotalAlerts()}</div>
            <div className="text-sm text-gray-600">Total Low Stock Items</div>
          </div>
        </div>
      </div>

      {/* Key Items Grid */}
      <div className="space-y-4">
        {keyItems.map((item) => (
          <div key={item.name} className="bg-white rounded-lg shadow-md overflow-hidden">
            {/* Item Header */}
            <div 
              className={`p-6 cursor-pointer transition-colors hover:bg-gray-50 ${
                item.low_stock_count > 0 ? 'border-l-4 border-red-500' : 'border-l-4 border-green-500'
              }`}
              onClick={() => toggleExpanded(item.name)}
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <div className="mr-4">
                    {item.low_stock_count > 0 ? (
                      <AlertTriangle className="w-6 h-6 text-red-600" />
                    ) : (
                      <Package className="w-6 h-6 text-green-600" />
                    )}
                  </div>
                  <div>
                    <h3 className="text-xl font-semibold text-gray-800">{item.name}</h3>
                    <p className="text-gray-600">
                      {item.low_stock_count > 0 
                        ? `${item.low_stock_count} variants need attention`
                        : 'All variants have adequate stock'
                      }
                    </p>
                  </div>
                </div>
                <div className="flex items-center">
                  {item.low_stock_count > 0 && (
                    <span className="bg-red-100 text-red-800 px-3 py-1 rounded-full text-sm font-medium mr-4">
                      {item.low_stock_count} alerts
                    </span>
                  )}
                  {expandedItems[item.name] ? (
                    <ChevronUp className="w-5 h-5 text-gray-400" />
                  ) : (
                    <ChevronDown className="w-5 h-5 text-gray-400" />
                  )}
                </div>
              </div>
            </div>

            {/* Expanded Content */}
            {expandedItems[item.name] && (
              <div className="border-t border-gray-200">
                {loadingAlerts[item.name] ? (
                  <div className="p-6 text-center">
                    <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                    <p className="text-gray-600 mt-2">Loading alerts for {item.name}...</p>
                  </div>
                ) : alerts[item.name] && alerts[item.name].length > 0 ? (
                  <div className="p-6">
                    <div className="flex items-center justify-between mb-4">
                      <h4 className="text-lg font-semibold text-gray-800">
                        Low Stock Details - {item.name}
                      </h4>
                      <Mail className="w-5 h-5 text-blue-600" />
                    </div>
                    <div className="overflow-x-auto">
                      <table className="w-full">
                        <thead className="bg-gray-50">
                          <tr>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Color</th>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Size</th>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Current Stock</th>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Required</th>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Shortage</th>
                            <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Priority</th>
                          </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                          {alerts[item.name].map((alert, index) => {
                            const urgency = getUrgencyLevel(alert.shortage);
                            return (
                              <tr key={index} className="hover:bg-gray-50">
                                <td className="px-4 py-3 text-sm font-medium text-gray-900">{alert.color}</td>
                                <td className="px-4 py-3 text-sm text-gray-700">{alert.size}</td>
                                <td className="px-4 py-3 text-sm text-gray-700">{alert.current_stock}</td>
                                <td className="px-4 py-3 text-sm text-gray-700">{alert.required_threshold}</td>
                                <td className="px-4 py-3 text-sm font-semibold text-red-600">-{alert.shortage}</td>
                                <td className="px-4 py-3">
                                  <span className={`px-2 py-1 text-xs font-medium rounded-full ${urgency.bg} ${urgency.color}`}>
                                    {urgency.label}
                                  </span>
                                </td>
                              </tr>
                            );
                          })}
                        </tbody>
                      </table>
                    </div>
                  </div>
                ) : (
                  <div className="p-6 text-center text-gray-600">
                    <Package className="w-12 h-12 text-green-500 mx-auto mb-2" />
                    <p>All variants for {item.name} have adequate stock levels</p>
                  </div>
                )}
              </div>
            )}
          </div>
        ))}
      </div>

      {keyItems.length === 0 && (
        <div className="bg-white rounded-lg shadow-md p-12 text-center">
          <Package className="w-16 h-16 text-gray-400 mx-auto mb-4" />
          <h3 className="text-xl font-semibold text-gray-700 mb-2">No Inventory Data</h3>
          <p className="text-gray-600">
            Upload an inventory report to see key items stock levels and alerts
          </p>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
