import React, { useState, useEffect } from 'react';
import { BarChart3, AlertTriangle, CheckCircle, Clock, Mail, TrendingDown } from 'lucide-react';
import { getTodayAlerts, getUploadHistory } from '../services/api';

const Dashboard = () => {
  const [todayAlerts, setTodayAlerts] = useState(null);
  const [uploadHistory, setUploadHistory] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      const [alertsData, historyData] = await Promise.all([
        getTodayAlerts(),
        getUploadHistory()
      ]);

      setTodayAlerts(alertsData);
      setUploadHistory(historyData.uploads || []);
    } catch (err) {
      setError('Failed to load dashboard data');
      console.error('Dashboard error:', err);
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-danier-gold"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="bg-red-50 border border-red-200 rounded-lg p-4">
        <p className="text-red-800">{error}</p>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-2xl font-bold text-danier-dark">Dashboard</h2>
            <p className="text-gray-600">
              Today's inventory status and recent activity
            </p>
          </div>
          <div className="text-right">
            <p className="text-sm text-gray-500">Today</p>
            <p className="text-lg font-semibold text-danier-dark">
              {new Date().toLocaleDateString('en-US', {
                weekday: 'long',
                year: 'numeric',
                month: 'long',
                day: 'numeric'
              })}
            </p>
          </div>
        </div>
      </div>

      {/* Status Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center">
            <div className="p-2 bg-blue-100 rounded-lg">
              <BarChart3 className="w-6 h-6 text-blue-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Last Upload</p>
              <p className="text-lg font-semibold text-danier-dark">
                {todayAlerts?.upload_date ? formatDate(todayAlerts.upload_date) : 'No uploads today'}
              </p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center">
            <div className="p-2 bg-red-100 rounded-lg">
              <AlertTriangle className="w-6 h-6 text-red-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Low Stock Items</p>
              <p className="text-lg font-semibold text-red-600">
                {todayAlerts?.total_items || 0}
              </p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center">
            <div className="p-2 bg-green-100 rounded-lg">
              <Mail className="w-6 h-6 text-green-600" />
            </div>
            <div className="ml-4">
              <p className="text-sm font-medium text-gray-600">Email Status</p>
              <p className="text-lg font-semibold text-green-600">
                {todayAlerts?.email_sent ? 'Sent' : 'Not Sent'}
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Today's Alerts */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex items-center justify-between mb-6">
          <h3 className="text-xl font-bold text-danier-dark">Today's Low Stock Alerts</h3>
          <button
            onClick={fetchDashboardData}
            className="text-danier-gold hover:text-yellow-600 text-sm font-medium"
          >
            Refresh
          </button>
        </div>

        {todayAlerts?.low_stock_items && todayAlerts.low_stock_items.length > 0 ? (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Item Description
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Stock Level
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {todayAlerts.low_stock_items.map((item, index) => (
                  <tr key={index} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                      {item.item_description}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm">
                      <span
                        className={`font-medium ${
                          item.stock < 50
                            ? 'text-red-600'
                            : item.stock < 80
                            ? 'text-orange-600'
                            : 'text-yellow-600'
                        }`}
                      >
                        {item.stock}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm">
                      {item.stock < 50 ? (
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-red-100 text-red-800">
                          Critical
                        </span>
                      ) : item.stock < 80 ? (
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-orange-100 text-orange-800">
                          Low
                        </span>
                      ) : (
                        <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                          Warning
                        </span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ) : (
          <div className="text-center py-8">
            <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
            <h4 className="text-lg font-medium text-green-600 mb-2">
              All items above threshold
            </h4>
            <p className="text-gray-500">
              No low stock items found in today's report
            </p>
          </div>
        )}
      </div>

      {/* Recent Upload History */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <h3 className="text-xl font-bold text-danier-dark mb-6">Recent Upload History</h3>
        <div className="space-y-4">
          {uploadHistory.length > 0 ? (
            uploadHistory.map((upload) => (
              <div
                key={upload.id}
                className="flex items-center justify-between p-4 border border-gray-200 rounded-lg"
              >
                <div className="flex items-center space-x-4">
                  <div className="p-2 bg-gray-100 rounded-lg">
                    <Clock className="w-5 h-5 text-gray-600" />
                  </div>
                  <div>
                    <p className="font-medium text-danier-dark">
                      {formatDate(upload.date)}
                    </p>
                    <p className="text-sm text-gray-500">
                      {upload.success ? 'Successfully processed' : 'Failed to process'}
                    </p>
                  </div>
                </div>
                <div className="flex items-center space-x-4">
                  {upload.success && (
                    <div className="flex items-center space-x-2">
                      <TrendingDown className="w-4 h-4 text-red-500" />
                      <span className="text-sm text-gray-600">
                        {upload.number_of_low_stock_items} low stock items
                      </span>
                    </div>
                  )}
                  {upload.email_sent && (
                    <div className="flex items-center space-x-2">
                      <Mail className="w-4 h-4 text-green-500" />
                      <span className="text-sm text-green-600">Email sent</span>
                    </div>
                  )}
                  {!upload.success && (
                    <span className="text-sm text-red-600">
                      {upload.error_message}
                    </span>
                  )}
                </div>
              </div>
            ))
          ) : (
            <div className="text-center py-8 text-gray-500">
              No upload history available
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
