import React, { useState } from 'react';
import { Search, AlertTriangle, Package } from 'lucide-react';

const SearchBar = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [isSearching, setIsSearching] = useState(false);
  const [hasSearched, setHasSearched] = useState(false);

  const handleSearch = async () => {
    if (!searchTerm.trim()) return;

    setIsSearching(true);
    setHasSearched(true);

    try {
      const response = await fetch(`https://danier-automated-system-production.up.railway.app/search/article/${encodeURIComponent(searchTerm)}`);
      const data = await response.json();

      if (response.ok) {
        setSearchResults(data.results || []);
      } else {
        console.error('Search failed:', data.detail);
        setSearchResults([]);
      }
    } catch (error) {
      console.error('Search error:', error);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSearch();
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6 mb-6">
      <h2 className="text-xl font-semibold mb-4 flex items-center">
        <Search className="w-5 h-5 mr-2 text-blue-600" />
        Search Article Alerts
      </h2>

      <div className="flex gap-2 mb-4">
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyPress={handleKeyPress}
          placeholder="Enter article name, style code, or color..."
          className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        />
        <button
          onClick={handleSearch}
          disabled={isSearching || !searchTerm.trim()}
          className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center"
        >
          {isSearching ? (
            <>
              <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
              Searching...
            </>
          ) : (
            <>
              <Search className="w-4 h-4 mr-2" />
              Search
            </>
          )}
        </button>
      </div>

      {hasSearched && (
        <div className="mt-4">
          {searchResults.length > 0 ? (
            <div>
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-medium text-gray-900">
                  Found {searchResults.length} matching items
                </h3>
                <span className="text-sm text-gray-500">
                  Search term: "{searchTerm}"
                </span>
              </div>

              <div className="space-y-3 max-h-96 overflow-y-auto">
                {searchResults.map((item, index) => (
                  <div key={index} className="border border-gray-200 rounded-lg p-4 hover:bg-gray-50">
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <h4 className="font-medium text-gray-900">{item.item_name}</h4>
                        <p className="text-sm text-gray-600 mt-1">{item.item_description}</p>
                        <div className="flex items-center gap-4 mt-2 text-sm">
                          <span className="flex items-center">
                            <Package className="w-4 h-4 mr-1 text-gray-500" />
                            {item.color} - {item.size}
                          </span>
                          <span className="flex items-center">
                            <AlertTriangle className="w-4 h-4 mr-1 text-red-500" />
                            Stock: {item.current_stock} (Need: {item.required_threshold})
                          </span>
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="text-lg font-bold text-red-600">
                          -{item.shortage}
                        </div>
                        <div className="text-xs text-gray-500">
                          Shortage
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ) : (
            <div className="text-center py-8">
              <Search className="w-12 h-12 text-gray-400 mx-auto mb-4" />
              <h3 className="text-lg font-medium text-gray-900 mb-2">No results found</h3>
              <p className="text-gray-500">
                No items found matching "{searchTerm}". Try a different search term.
              </p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchBar;
