import React, { useState } from 'react';
import { Upload, CheckCircle, AlertCircle, ArrowRight } from 'lucide-react';
import { uploadReport } from '../services/api';
import { useNavigate } from 'react-router-dom';

const UploadPage = () => {
  const [file, setFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploadResult, setUploadResult] = useState(null);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') {
      setFile(selectedFile);
      setError('');
    } else {
      setError('Please select a valid Excel file (.xlsx)');
      setFile(null);
    }
  };

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a file first');
      return;
    }

    setUploading(true);
    setError('');
    setUploadResult(null);

    try {
      const result = await uploadReport(file);
      setUploadResult(result);

      // Redirect to dashboard after successful upload
      setTimeout(() => {
        navigate('/');
      }, 2000);

    } catch (err) {
      setError(err.response?.data?.detail || 'Upload failed. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <div className="bg-white rounded-lg shadow-lg p-8">
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Upload Inventory Report</h1>
          <p className="text-gray-600">Upload your daily inventory Excel file to check key items stock levels</p>
        </div>

        {/* Upload Area */}
        <div className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center mb-6">
          <Upload className="w-12 h-12 text-gray-400 mx-auto mb-4" />
          <p className="text-gray-600 mb-4">Drag and drop your Excel file here, or click to browse</p>
          <input
            type="file"
            accept=".xlsx"
            onChange={handleFileChange}
            className="hidden"
            id="file-upload"
          />
          <label
            htmlFor="file-upload"
            className="bg-blue-600 text-white px-6 py-2 rounded-lg cursor-pointer hover:bg-blue-700 transition-colors"
          >
            Choose File
          </label>
          {file && (
            <div className="mt-4 p-3 bg-green-50 rounded-lg">
              <CheckCircle className="w-5 h-5 text-green-600 inline mr-2" />
              <span className="text-green-800">{file.name}</span>
            </div>
          )}
        </div>

        {/* Error Display */}
        {error && (
          <div className="mb-6 p-4 bg-red-50 border border-red-200 rounded-lg">
            <AlertCircle className="w-5 h-5 text-red-600 inline mr-2" />
            <span className="text-red-800">{error}</span>
          </div>
        )}

        {/* Upload Button */}
        <div className="text-center">
          <button
            onClick={handleUpload}
            disabled={!file || uploading}
            className={`px-8 py-3 rounded-lg font-medium transition-colors ${
              !file || uploading
                ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                : 'bg-green-600 text-white hover:bg-green-700'
            }`}
          >
            {uploading ? 'Uploading...' : 'Upload Report'}
          </button>
        </div>

        {/* Success Result */}
        {uploadResult && (
          <div className="mt-8 p-6 bg-green-50 border border-green-200 rounded-lg">
            <div className="flex items-center mb-4">
              <CheckCircle className="w-6 h-6 text-green-600 mr-2" />
              <h3 className="text-lg font-semibold text-green-800">Upload Successful!</h3>
            </div>

            <p className="text-green-800 font-medium">
              {uploadResult.message}
            </p>

            {uploadResult.file_processed && (
              <p className="text-sm text-green-700 mt-2">
                File: {uploadResult.file_processed}
              </p>
            )}

            {uploadResult.email_sent && (
              <div className="mt-4">
                <p className="text-sm text-green-700">
                  ✅ Email alert sent to {uploadResult.recipient}
                </p>
              </div>
            )}

            <div className="mt-4 p-4 bg-blue-50 rounded-lg">
              <div className="flex items-center">
                <ArrowRight className="w-5 h-5 text-blue-600 mr-2" />
                <p className="text-blue-800">
                  Redirecting to dashboard to view key items...
                </p>
              </div>
            </div>
          </div>
        )}

        {/* Results Display */}
        {uploadResult && uploadResult.results_by_item && Object.keys(uploadResult.results_by_item).length > 0 && (
          <div className="mt-8">
            <h3 className="text-xl font-semibold text-gray-800 mb-4">Low Stock Items Found:</h3>
            {Object.entries(uploadResult.results_by_item).map(([itemName, alerts]) => (
              <div key={itemName} className="mb-6 bg-white border border-gray-200 rounded-lg overflow-hidden">
                <div className="bg-gray-50 px-4 py-3 border-b border-gray-200">
                  <h4 className="font-semibold text-gray-800">{itemName}</h4>
                  <p className="text-sm text-gray-600">{alerts.length} low stock variants</p>
                </div>
                <div className="overflow-x-auto">
                  <table className="w-full">
                    <thead className="bg-gray-50">
                      <tr>
                        <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Color</th>
                        <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Size</th>
                        <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Current Stock</th>
                        <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Required</th>
                        <th className="px-4 py-2 text-left text-sm font-medium text-gray-700">Shortage</th>
                      </tr>
                    </thead>
                    <tbody>
                      {alerts.map((alert, index) => (
                        <tr key={index} className="border-b border-gray-100">
                          <td className="px-4 py-2 text-sm text-gray-800">{alert.color}</td>
                          <td className="px-4 py-2 text-sm text-gray-800">{alert.size}</td>
                          <td className="px-4 py-2 text-sm text-gray-800">{alert.current_stock}</td>
                          <td className="px-4 py-2 text-sm text-gray-800">{alert.required_threshold}</td>
                          <td className="px-4 py-2 text-sm text-red-600 font-medium">{alert.shortage}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default UploadPage;
