import React, { useState, useEffect, useCallback } from 'react';
import { Calendar, FileText, Package, AlertTriangle, TrendingUp, Database, BarChart3, GitCompare, Star, XCircle, CheckCircle } from 'lucide-react';

const InventoryFilesDashboard = () => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileAlerts, setFileAlerts] = useState(null);
  const [loading, setLoading] = useState(true);
  const [allKeyItems, setAllKeyItems] = useState([]);
  const [comparisonFiles, setComparisonFiles] = useState([]);
  const [comparisonResult, setComparisonResult] = useState(null);
  const [performanceAnalysis, setPerformanceAnalysis] = useState(null);
  const [activeTab, setActiveTab] = useState('files');
  const [analysisReady, setAnalysisReady] = useState(false);

  const fetchInventoryFiles = useCallback(async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/files/enhanced-list');
      const data = await response.json();
      setFiles(data.files || []);
      setAllKeyItems(data.all_key_items || []);
      setAnalysisReady(data.analysis_ready || false);

      // Select the most recent file by default
      if (data.files && data.files.length > 0) {
        setSelectedFile(data.files[0]);
        fetchFileAlerts(data.files[0].filename);
      }
    } catch (error) {
      console.error('Error fetching inventory files:', error);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchInventoryFiles();
  }, [fetchInventoryFiles]);

  const fetchFileAlerts = async (filename) => {
    try {
      const response = await fetch(`/api/inventory-files/${filename}/alerts`);
      const data = await response.json();
      setFileAlerts(data);
    } catch (error) {
      console.error('Error fetching file alerts:', error);
    }
  };

  const fetchPerformanceAnalysis = async () => {
    try {
      const response = await fetch('/api/files/performance-analysis');
      const data = await response.json();
      setPerformanceAnalysis(data);
    } catch (error) {
      console.error('Error fetching performance analysis:', error);
    }
  };

  const compareFiles = async () => {
    if (comparisonFiles.length !== 2) return;

    try {
      const [file1, file2] = comparisonFiles;
      const response = await fetch(`/api/files/compare/${file1.filename}/${file2.filename}`);
      const data = await response.json();
      setComparisonResult(data);
    } catch (error) {
      console.error('Error comparing files:', error);
    }
  };

  const handleFileSelect = (file) => {
    setSelectedFile(file);
    fetchFileAlerts(file.filename);
  };

  const handleComparisonSelect = (file) => {
    if (comparisonFiles.find(f => f.filename === file.filename)) {
      setComparisonFiles(comparisonFiles.filter(f => f.filename !== file.filename));
    } else if (comparisonFiles.length < 2) {
      setComparisonFiles([...comparisonFiles, file]);
    }
  };

  const formatDate = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleDateString();
  };

  const formatDateTime = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  const getRecommendationBadge = (recommendation) => {
    const badges = {
      'STRONG_PERFORMER': { color: 'bg-green-100 text-green-800', icon: <Star className="w-4 h-4" />, text: 'Strong Performer' },
      'RELIABLE_PERFORMER': { color: 'bg-blue-100 text-blue-800', icon: <CheckCircle className="w-4 h-4" />, text: 'Reliable' },
      'CONSIDER_DISCONTINUING': { color: 'bg-red-100 text-red-800', icon: <XCircle className="w-4 h-4" />, text: 'Consider Discontinuing' },
      'INCONSISTENT_DEMAND': { color: 'bg-yellow-100 text-yellow-800', icon: <AlertTriangle className="w-4 h-4" />, text: 'Inconsistent' },
      'REVIEW_STOCKING_LEVELS': { color: 'bg-orange-100 text-orange-800', icon: <Package className="w-4 h-4" />, text: 'Review Stocking' },
      'MONITOR_CLOSELY': { color: 'bg-purple-100 text-purple-800', icon: <TrendingUp className="w-4 h-4" />, text: 'Monitor' }
    };

    const badge = badges[recommendation] || { color: 'bg-gray-100 text-gray-800', icon: null, text: recommendation };

    return (
      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${badge.color}`}>
        {badge.icon && <span className="mr-1">{badge.icon}</span>}
        {badge.text}
      </span>
    );
  };

  const getStatusBadge = (status) => {
    const colors = {
      'INCREASED': 'bg-green-100 text-green-800',
      'DECREASED': 'bg-red-100 text-red-800',
      'NEW_ITEM': 'bg-blue-100 text-blue-800',
      'DISCONTINUED': 'bg-gray-100 text-gray-800',
      'NO_CHANGE': 'bg-yellow-100 text-yellow-800'
    };

    return (
      <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${colors[status] || 'bg-gray-100 text-gray-800'}`}>
        {status.replace('_', ' ')}
      </span>
    );
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto p-6">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
            <p className="mt-2 text-gray-600">Loading inventory files...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto p-6">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <Database className="w-8 h-8 text-blue-600 mr-3" />
            <div>
              <h1 className="text-3xl font-bold text-gray-800">Multi-File Analysis Dashboard</h1>
              <p className="text-gray-600">Compare inventory files and analyze product performance over time</p>
            </div>
          </div>
          <div className="text-right">
            <div className="text-2xl font-bold text-blue-600">{files.length}</div>
            <div className="text-sm text-gray-500">Files Available</div>
          </div>
        </div>
      </div>

      {/* Navigation Tabs */}
      <div className="bg-white rounded-lg shadow-md mb-6">
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex">
            <button
              onClick={() => setActiveTab('files')}
              className={`py-4 px-6 border-b-2 font-medium text-sm ${
                activeTab === 'files'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <FileText className="w-5 h-5 inline mr-2" />
              File Management
            </button>
            <button
              onClick={() => setActiveTab('comparison')}
              className={`py-4 px-6 border-b-2 font-medium text-sm ${
                activeTab === 'comparison'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
            >
              <GitCompare className="w-5 h-5 inline mr-2" />
              File Comparison
            </button>
            <button
              onClick={() => {
                setActiveTab('performance');
                if (analysisReady && !performanceAnalysis) {
                  fetchPerformanceAnalysis();
                }
              }}
              className={`py-4 px-6 border-b-2 font-medium text-sm ${
                activeTab === 'performance'
                  ? 'border-blue-500 text-blue-600'
                  : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
              }`}
              disabled={!analysisReady}
            >
              <BarChart3 className="w-5 h-5 inline mr-2" />
              Performance Analysis
              {!analysisReady && <span className="ml-2 text-xs text-gray-400">(Need 2+ files)</span>}
            </button>
          </nav>
        </div>

        {/* Tab Content */}
        <div className="p-6">
          {activeTab === 'files' && (
            <div className="space-y-6">
              {/* File List */}
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
                <div>
                  <h3 className="text-lg font-semibold text-gray-800 mb-4">Uploaded Files</h3>
                  <div className="space-y-3">
                    {files.map((file) => (
                      <div
                        key={file.filename}
                        onClick={() => handleFileSelect(file)}
                        className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                          selectedFile?.filename === file.filename
                            ? 'border-blue-500 bg-blue-50'
                            : 'border-gray-200 hover:border-gray-300'
                        }`}
                      >
                        <div className="flex items-center justify-between">
                          <div>
                            <h4 className="font-medium text-gray-800">{file.filename}</h4>
                            <p className="text-sm text-gray-600">
                              {formatDateTime(file.upload_date)}  {(file.file_size / 1024).toFixed(1)} KB
                            </p>
                            <p className="text-sm text-gray-600">
                              {file.key_items_count} KI00 items  {file.low_stock_count} low stock alerts
                            </p>
                          </div>
                          <div className="text-right">
                            {file.low_stock_count > 0 && (
                              <span className="bg-red-100 text-red-800 text-xs font-medium px-2 py-1 rounded-full">
                                {file.low_stock_count} alerts
                              </span>
                            )}
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                {/* File Details */}
                <div>
                  <h3 className="text-lg font-semibold text-gray-800 mb-4">File Details</h3>
                  {selectedFile ? (
                    <div className="bg-gray-50 rounded-lg p-4">
                      <h4 className="font-medium text-gray-800 mb-3">{selectedFile.filename}</h4>
                      <div className="space-y-2 text-sm">
                        <div className="flex justify-between">
                          <span className="text-gray-600">Upload Date:</span>
                          <span className="text-gray-800">{formatDateTime(selectedFile.upload_date)}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">File Size:</span>
                          <span className="text-gray-800">{(selectedFile.file_size / 1024).toFixed(1)} KB</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">KI00 Items:</span>
                          <span className="text-gray-800">{selectedFile.key_items_count}</span>
                        </div>
                        <div className="flex justify-between">
                          <span className="text-gray-600">Low Stock Alerts:</span>
                          <span className="text-gray-800">{selectedFile.low_stock_count}</span>
                        </div>
                      </div>

                      {selectedFile.key_items && selectedFile.key_items.length > 0 && (
                        <div className="mt-4">
                          <h5 className="font-medium text-gray-700 mb-2">KI00 Items in this file:</h5>
                          <div className="flex flex-wrap gap-1">
                            {selectedFile.key_items.slice(0, 10).map((item, index) => (
                              <span key={index} className="bg-blue-100 text-blue-800 text-xs px-2 py-1 rounded">
                                {item}
                              </span>
                            ))}
                            {selectedFile.key_items.length > 10 && (
                              <span className="bg-gray-100 text-gray-600 text-xs px-2 py-1 rounded">
                                +{selectedFile.key_items.length - 10} more
                              </span>
                            )}
                          </div>
                        </div>
                      )}
                    </div>
                  ) : (
                    <div className="bg-gray-50 rounded-lg p-4 text-center text-gray-600">
                      Select a file to view details
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {activeTab === 'comparison' && (
            <div className="space-y-6">
              <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                <h3 className="text-lg font-semibold text-blue-800 mb-2">File Comparison</h3>
                <p className="text-blue-700 text-sm">
                  Select exactly 2 files to compare stock level changes. This will help identify which products are performing well or need attention.
                </p>
              </div>

              {/* File Selection for Comparison */}
              <div>
                <h4 className="font-medium text-gray-800 mb-3">
                  Select Files to Compare ({comparisonFiles.length}/2 selected)
                </h4>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {files.map((file) => (
                    <div
                      key={file.filename}
                      onClick={() => handleComparisonSelect(file)}
                      className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                        comparisonFiles.find(f => f.filename === file.filename)
                          ? 'border-green-500 bg-green-50'
                          : 'border-gray-200 hover:border-gray-300'
                      }`}
                    >
                      <div className="flex items-center justify-between">
                        <div>
                          <h5 className="font-medium text-gray-800">{file.filename}</h5>
                          <p className="text-sm text-gray-600">{formatDateTime(file.upload_date)}</p>
                        </div>
                        {comparisonFiles.find(f => f.filename === file.filename) && (
                          <CheckCircle className="w-5 h-5 text-green-600" />
                        )}
                      </div>
                    </div>
                  ))}
                </div>

                {comparisonFiles.length === 2 && (
                  <div className="mt-4 text-center">
                    <button
                      onClick={compareFiles}
                      className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                    >
                      Compare Files
                    </button>
                  </div>
                )}
              </div>

              {/* Comparison Results */}
              {comparisonResult && (
                <div className="space-y-4">
                  <h4 className="font-medium text-gray-800">Comparison Results</h4>

                  {/* Summary */}
                  <div className="bg-gray-50 rounded-lg p-4">
                    <h5 className="font-medium text-gray-700 mb-3">Summary</h5>
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                      <div>
                        <span className="text-gray-600">Total Items:</span>
                        <div className="font-medium text-gray-800">{comparisonResult.summary?.total_items || 0}</div>
                      </div>
                      <div>
                        <span className="text-green-600">Increased:</span>
                        <div className="font-medium text-green-800">{comparisonResult.summary?.increased_stock || 0}</div>
                      </div>
                      <div>
                        <span className="text-red-600">Decreased:</span>
                        <div className="font-medium text-red-800">{comparisonResult.summary?.decreased_stock || 0}</div>
                      </div>
                      <div>
                        <span className="text-blue-600">New Items:</span>
                        <div className="font-medium text-blue-800">{comparisonResult.summary?.new_items || 0}</div>
                      </div>
                    </div>
                  </div>

                  {/* Detailed Changes */}
                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Item</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Color</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Size</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Old Stock</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">New Stock</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Change</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {(comparisonResult.comparisons || []).slice(0, 50).map((comparison, index) => (
                          <tr key={index}>
                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                              {comparison.item_name}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                              {comparison.color}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                              {comparison.size}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                              {comparison.old_stock}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                              {comparison.new_stock}
                            </td>
                            <td className={`px-6 py-4 whitespace-nowrap text-sm font-medium ${
                              comparison.change > 0 ? 'text-green-600' : 
                              comparison.change < 0 ? 'text-red-600' : 'text-gray-600'
                            }`}>
                              {comparison.change > 0 ? '+' : ''}{comparison.change}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm">
                              {getStatusBadge(comparison.status)}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === 'performance' && (
            <div className="space-y-6">
              {!analysisReady ? (
                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 text-center">
                  <AlertTriangle className="w-8 h-8 text-yellow-600 mx-auto mb-2" />
                  <h3 className="text-lg font-medium text-yellow-800">Analysis Not Available</h3>
                  <p className="text-yellow-700">Upload at least 2 inventory files to enable performance analysis.</p>
                </div>
              ) : !performanceAnalysis ? (
                <div className="text-center">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
                  <p className="mt-2 text-gray-600">Analyzing performance...</p>
                </div>
              ) : (
                <div className="space-y-6">
                  {/* Performance Summary */}
                  <div className="bg-gray-50 rounded-lg p-6">
                    <h3 className="text-lg font-semibold text-gray-800 mb-4">Performance Analysis Summary</h3>
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                      <div className="text-center">
                        <div className="text-2xl font-bold text-blue-600">{performanceAnalysis.total_products_analyzed}</div>
                        <div className="text-sm text-gray-600">Products Analyzed</div>
                      </div>
                      <div className="text-center">
                        <div className="text-2xl font-bold text-green-600">{performanceAnalysis.summary?.strong_performers || 0}</div>
                        <div className="text-sm text-gray-600">Strong Performers</div>
                      </div>
                      <div className="text-center">
                        <div className="text-2xl font-bold text-red-600">{performanceAnalysis.summary?.consider_discontinuing || 0}</div>
                        <div className="text-sm text-gray-600">Consider Discontinuing</div>
                      </div>
                      <div className="text-center">
                        <div className="text-2xl font-bold text-purple-600">{performanceAnalysis.summary?.average_consistency_score?.toFixed(1) || 0}%</div>
                        <div className="text-sm text-gray-600">Avg Consistency</div>
                      </div>
                    </div>
                  </div>

                  {/* Performance Details */}
                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Product</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Color/Size</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Trend</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Avg Stock</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Consistency</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Recommendation</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {(performanceAnalysis.performance_analysis || []).map((item, index) => (
                          <tr key={index}>
                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                              {item.item_name}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                              {item.color} / {item.size}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm">
                              <span className={`font-medium ${
                                item.trend_direction === 'INCREASING' ? 'text-green-600' :
                                item.trend_direction === 'DECREASING' ? 'text-red-600' : 'text-gray-600'
                              }`}>
                                {item.trend_direction}
                              </span>
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                              {item.average_stock}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                              {item.consistency_score}%
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm">
                              {getRecommendationBadge(item.recommendation)}
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default InventoryFilesDashboard;
