import React, { useState, useEffect } from 'react';
import { ChevronDown, ChevronRight, AlertTriangle, Package } from 'lucide-react';
import { getAllKeyItemsWithAlerts } from '../services/api';

const KeyItemsDashboard = () => {
  const [keyItems, setKeyItems] = useState([]);
  const [expandedItems, setExpandedItems] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadAllKeyItemsWithAlerts();
  }, []);

  const loadAllKeyItemsWithAlerts = async () => {
    try {
      setLoading(true);
      console.log('ðŸš€ Loading key items summary...');
      // first get lightweight summary for fast paint
      const res = await fetch('http://localhost:8000/key-items/summary');
      const summary = await res.json();
      if (summary && Array.isArray(summary.key_items)) {
        setKeyItems(summary.key_items);
      }
      console.log('âš¡ Summary loaded, fetching full alerts in background...');
      // fetch full alerts in background to keep UI responsive
      const response = await getAllKeyItemsWithAlerts();
      if (!response.error && Array.isArray(response.key_items)) {
        setKeyItems(response.key_items);
      }
    } catch (err) {
      setError('Failed to load key items');
      console.error('âŒ Error loading key items:', err);
    } finally {
      setLoading(false);
    }
  };

  const toggleItem = (itemName) => {
    setExpandedItems(prev => ({ ...prev, [itemName]: !prev[itemName] }));
  };

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto"></div>
            <p className="mt-2 text-gray-600">Loading key items with alerts...</p>
            <p className="text-sm text-gray-500">Ultra-fast batch processing</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="text-center text-red-600">
            <AlertTriangle className="w-8 h-8 mx-auto mb-2" />
            <p>{error}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-gray-800 mb-2">
            Key Items Dashboard
          </h2>
          <p className="text-gray-600">
            Click on any key item to view detailed stock alerts
          </p>
          <p className="text-sm text-green-600 mt-2">
            âš¡ Ultra-fast batch processing â€¢ {keyItems.length} items loaded
          </p>
        </div>

        <div className="space-y-4">
          {keyItems.map((item) => {
            const itemName = item.name || item?.name;
            const alertCount = item.alert_count || item?.alert_count || 0;
            const alerts = item.alerts || [];

            return (
              <div key={itemName} className="border border-gray-200 rounded-lg overflow-hidden">
                <button
                  onClick={() => toggleItem(itemName)}
                  className="w-full px-6 py-4 bg-gray-50 hover:bg-gray-100 flex items-center justify-between text-left transition-colors"
                >
                  <div className="flex items-center">
                    <Package className="w-5 h-5 text-blue-600 mr-3" />
                    <span className="font-medium text-gray-800">{itemName}</span>
                    {alertCount > 0 && (
                      <span className="ml-3 px-2 py-1 bg-red-100 text-red-800 text-xs font-medium rounded-full">
                        {alertCount} alerts
                      </span>
                    )}
                  </div>
                  <div className="flex items-center">
                    {expandedItems[itemName] ? (
                      <ChevronDown className="w-5 h-5 text-gray-500" />
                    ) : (
                      <ChevronRight className="w-5 h-5 text-gray-500" />
                    )}
                  </div>
                </button>

                {expandedItems[itemName] && alerts.length > 0 && (
                  <div className="px-6 py-4 bg-white border-t border-gray-200">
                    <div className="space-y-3">
                      <h4 className="font-medium text-gray-700 mb-3">Low Stock Alerts:</h4>
                      {alerts.map((alert, index) => (
                        <div key={index} className="flex items-center justify-between p-3 bg-red-50 border border-red-200 rounded-lg">
                          <div className="flex items-center">
                            <AlertTriangle className="w-4 h-4 text-red-600 mr-2" />
                            <span className="font-medium text-gray-800">{alert.color || 'N/A'}</span>
                            <span className="mx-2 text-gray-500">â€¢</span>
                            <span className="text-gray-600">Size: {alert.size || 'N/A'}</span>
                          </div>
                          <div className="text-right">
                            <span className="text-red-600 font-bold">{alert.stock_level || alert.current_stock || 'N/A'}</span>
                            <span className="text-gray-500 text-sm ml-1">units</span>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default KeyItemsDashboard;
