import React, { useState, useEffect } from 'react';
import { Users, Plus, Trash2, Mail, User } from 'lucide-react';
import { getRecipients, addRecipient, deleteRecipient } from '../services/api';

const Recipients = () => {
  const [recipients, setRecipients] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newEmail, setNewEmail] = useState('');
  const [newName, setNewName] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    fetchRecipients();
  }, []);

  const fetchRecipients = async () => {
    try {
      setLoading(true);
      const data = await getRecipients();
      setRecipients(data.recipients || []);
    } catch (err) {
      setError('Failed to load recipients');
      console.error('Recipients error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleAddRecipient = async (e) => {
    e.preventDefault();

    if (!newEmail.trim()) {
      setError('Email is required');
      return;
    }

    // Basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(newEmail)) {
      setError('Please enter a valid email address');
      return;
    }

    try {
      setIsSubmitting(true);
      setError(null);

      await addRecipient(newEmail, newName.trim() || null);

      // Reset form
      setNewEmail('');
      setNewName('');
      setShowAddForm(false);

      // Refresh recipients list
      await fetchRecipients();
    } catch (err) {
      setError(err.response?.data?.detail || 'Failed to add recipient');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleDeleteRecipient = async (recipientId) => {
    if (!window.confirm('Are you sure you want to remove this recipient?')) {
      return;
    }

    try {
      await deleteRecipient(recipientId);
      await fetchRecipients();
    } catch (err) {
      setError('Failed to delete recipient');
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-danier-gold"></div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-2xl font-bold text-danier-dark">Email Recipients</h2>
            <p className="text-gray-600">
              Manage who receives low stock alert emails
            </p>
          </div>
          <button
            onClick={() => setShowAddForm(!showAddForm)}
            className="bg-danier-gold text-white px-4 py-2 rounded-md hover:bg-yellow-600 flex items-center"
          >
            <Plus className="w-4 h-4 mr-2" />
            Add Recipient
          </button>
        </div>

        {error && (
          <div className="mb-6 bg-red-50 border border-red-200 rounded-lg p-4">
            <p className="text-red-800">{error}</p>
          </div>
        )}

        {/* Add Recipient Form */}
        {showAddForm && (
          <div className="mb-6 bg-gray-50 border border-gray-200 rounded-lg p-6">
            <h3 className="text-lg font-medium text-danier-dark mb-4">Add New Recipient</h3>
            <form onSubmit={handleAddRecipient} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                    Email Address *
                  </label>
                  <input
                    type="email"
                    id="email"
                    value={newEmail}
                    onChange={(e) => setNewEmail(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-danier-gold focus:border-transparent"
                    placeholder="recipient@example.com"
                    required
                  />
                </div>
                <div>
                  <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">
                    Name (Optional)
                  </label>
                  <input
                    type="text"
                    id="name"
                    value={newName}
                    onChange={(e) => setNewName(e.target.value)}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-danier-gold focus:border-transparent"
                    placeholder="John Doe"
                  />
                </div>
              </div>
              <div className="flex space-x-4">
                <button
                  type="submit"
                  disabled={isSubmitting}
                  className="bg-danier-gold text-white px-4 py-2 rounded-md hover:bg-yellow-600 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
                >
                  {isSubmitting ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                      Adding...
                    </>
                  ) : (
                    <>
                      <Plus className="w-4 h-4 mr-2" />
                      Add Recipient
                    </>
                  )}
                </button>
                <button
                  type="button"
                  onClick={() => {
                    setShowAddForm(false);
                    setNewEmail('');
                    setNewName('');
                    setError(null);
                  }}
                  className="bg-gray-300 text-gray-700 px-4 py-2 rounded-md hover:bg-gray-400"
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Recipients List */}
        <div>
          <h3 className="text-lg font-medium text-danier-dark mb-4">
            Current Recipients ({recipients.length})
          </h3>

          {recipients.length > 0 ? (
            <div className="space-y-3">
              {recipients.map((recipient) => (
                <div
                  key={recipient.id}
                  className="flex items-center justify-between p-4 border border-gray-200 rounded-lg hover:bg-gray-50"
                >
                  <div className="flex items-center space-x-4">
                    <div className="p-2 bg-blue-100 rounded-lg">
                      <Mail className="w-5 h-5 text-blue-600" />
                    </div>
                    <div>
                      <p className="font-medium text-danier-dark">
                        {recipient.email}
                      </p>
                      {recipient.name && (
                        <p className="text-sm text-gray-500 flex items-center">
                          <User className="w-3 h-3 mr-1" />
                          {recipient.name}
                        </p>
                      )}
                      <p className="text-xs text-gray-400">
                        Added on {formatDate(recipient.created_at)}
                      </p>
                    </div>
                  </div>
                  <button
                    onClick={() => handleDeleteRecipient(recipient.id)}
                    className="text-red-600 hover:text-red-800 p-2 hover:bg-red-50 rounded-md transition-colors"
                    title="Remove recipient"
                  >
                    <Trash2 className="w-4 h-4" />
                  </button>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-8 bg-gray-50 border border-gray-200 rounded-lg">
              <Users className="w-16 h-16 text-gray-400 mx-auto mb-4" />
              <h4 className="text-lg font-medium text-gray-600 mb-2">
                No recipients added yet
              </h4>
              <p className="text-gray-500 mb-4">
                Add email recipients to receive low stock alerts
              </p>
              <button
                onClick={() => setShowAddForm(true)}
                className="bg-danier-gold text-white px-4 py-2 rounded-md hover:bg-yellow-600 flex items-center mx-auto"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add First Recipient
              </button>
            </div>
          )}
        </div>

        {/* Information Box */}
        <div className="mt-8 bg-blue-50 border border-blue-200 rounded-lg p-4">
          <h4 className="text-sm font-medium text-blue-800 mb-2">How it works</h4>
          <ul className="text-sm text-blue-700 space-y-1">
            <li>• All recipients will receive email alerts when low stock items are detected</li>
            <li>• Emails are sent automatically after each inventory report upload</li>
            <li>• Recipients can be added or removed at any time</li>
            <li>• Email addresses must be valid and unique</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Recipients;
