import React, { useState, useEffect } from 'react';
import { ChevronDown, ChevronRight, AlertTriangle, Package } from 'lucide-react';
import { getKeyItemsList, getSpecificKeyItemAlerts } from '../services/api';

const KeyItemsDashboard = () => {
  const [keyItems, setKeyItems] = useState([]);
  const [expandedItems, setExpandedItems] = useState({});
  const [itemAlerts, setItemAlerts] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadKeyItems();
  }, []);

  const loadKeyItems = async () => {
    try {
      setLoading(true);
      const response = await getKeyItemsList();
      setKeyItems(response.key_items || []);
    } catch (err) {
      setError('Failed to load key items');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const toggleItem = async (itemName) => {
    if (expandedItems[itemName]) {
      // Collapse
      setExpandedItems(prev => ({ ...prev, [itemName]: false }));
    } else {
      // Expand and load data
      setExpandedItems(prev => ({ ...prev, [itemName]: true }));

      if (!itemAlerts[itemName]) {
        try {
          const response = await getSpecificKeyItemAlerts(itemName);
          setItemAlerts(prev => ({ ...prev, [itemName]: response }));
        } catch (err) {
          console.error(`Failed to load alerts for ${itemName}:`, err);
          setItemAlerts(prev => ({ ...prev, [itemName]: { error: true } }));
        }
      }
    }
  };

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-danier-gold mx-auto"></div>
            <p className="mt-2 text-gray-600">Loading key items...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="text-center text-red-600">
            <AlertTriangle className="w-8 h-8 mx-auto mb-2" />
            <p>{error}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-danier-dark mb-2">
            Key Items Dashboard
          </h2>
          <p className="text-gray-600">
            Click on any key item to view detailed stock alerts
          </p>
        </div>

        <div className="space-y-4">
          {keyItems.map((itemName) => (
            <div key={itemName} className="border border-gray-200 rounded-lg overflow-hidden">
              <button
                onClick={() => toggleItem(itemName)}
                className="w-full px-6 py-4 bg-gray-50 hover:bg-gray-100 flex items-center justify-between text-left transition-colors"
              >
                <div className="flex items-center">
                  <Package className="w-5 h-5 text-danier-gold mr-3" />
                  <span className="text-lg font-semibold text-danier-dark">
                    {itemName}
                  </span>
                </div>
                <div className="flex items-center">
                  {itemAlerts[itemName] && !itemAlerts[itemName].error && (
                    <span className="bg-red-100 text-red-800 text-sm font-medium px-2 py-1 rounded-full mr-3">
                      {itemAlerts[itemName].total_alerts} alerts
                    </span>
                  )}
                  {expandedItems[itemName] ? (
                    <ChevronDown className="w-5 h-5 text-gray-500" />
                  ) : (
                    <ChevronRight className="w-5 h-5 text-gray-500" />
                  )}
                </div>
              </button>

              {expandedItems[itemName] && (
                <div className="border-t border-gray-200">
                  {itemAlerts[itemName]?.error ? (
                    <div className="p-4 text-center text-red-600">
                      Failed to load alerts for {itemName}
                    </div>
                  ) : itemAlerts[itemName]?.low_stock_items?.length > 0 ? (
                    <div className="overflow-x-auto">
                      <table className="min-w-full divide-y divide-gray-200">
                        <thead className="bg-gray-50">
                          <tr>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Color
                            </th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Size
                            </th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Current Stock
                            </th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Required
                            </th>
                            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                              Shortage
                            </th>
                          </tr>
                        </thead>
                        <tbody className="bg-white divide-y divide-gray-200">
                          {itemAlerts[itemName].low_stock_items.map((item, index) => (
                            <tr key={index}>
                              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                {item.color}
                              </td>
                              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                {item.size}
                              </td>
                              <td className="px-6 py-4 whitespace-nowrap text-sm">
                                <span className="font-medium text-red-600">
                                  {item.current_stock}
                                </span>
                              </td>
                              <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                {item.required_threshold}
                              </td>
                              <td className="px-6 py-4 whitespace-nowrap text-sm">
                                <span className="font-medium text-red-600">
                                  {item.shortage}
                                </span>
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  ) : (
                    <div className="p-4 text-center text-green-600">
                      âœ… All {itemName} items are above the threshold
                    </div>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default KeyItemsDashboard;
