import React from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useLocation } from 'react-router-dom';
import { Upload, BarChart3, Users, Settings, Package, Database } from 'lucide-react';
import UploadPage from './components/UploadPage';
import Dashboard from './components/Dashboard';
import Recipients from './components/Recipients';
import SettingsPage from './components/Settings';
import KeyItemsDashboard from './components/KeyItemsDashboard';
import InventoryFilesDashboard from './components/InventoryFilesDashboard';

function App() {
  return (
    <Router>
      <div className="min-h-screen bg-gray-50">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <div className="flex justify-between items-center h-16">
              <div className="flex items-center">
                <h1 className="text-2xl font-bold text-danier-dark">
                  Danier Stock Alert System
                </h1>
              </div>
              <div className="text-sm text-gray-500">
                Automated Inventory Management
              </div>
            </div>
          </div>
        </header>

        {/* Navigation */}
        <Navigation />

        {/* Main Content */}
        <main className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
          <Routes>
            <Route path="/" element={<UploadPage />} />
            <Route path="/dashboard" element={<Dashboard />} />
            <Route path="/recipients" element={<Recipients />} />
            <Route path="/settings" element={<SettingsPage />} />
            <Route path="/key-items" element={<KeyItemsDashboard />} />
            <Route path="/inventory-files" element={<InventoryFilesDashboard />} />
          </Routes>
        </main>
      </div>
    </Router>
  );
}

// Navigation Component
function Navigation() {
  const location = useLocation();

  const navItems = [
    { to: "/", icon: <Upload className="w-5 h-5" />, label: "Upload Report" },
    { to: "/dashboard", icon: <BarChart3 className="w-5 h-5" />, label: "Dashboard" },
    { to: "/key-items", icon: <Package className="w-5 h-5" />, label: "Key Items" },
    { to: "/inventory-files", icon: <Database className="w-5 h-5" />, label: "Multi-File" },
    { to: "/recipients", icon: <Users className="w-5 h-5" />, label: "Recipients" },
    { to: "/settings", icon: <Settings className="w-5 h-5" />, label: "Settings" }
  ];

  return (
    <nav className="bg-danier-gold">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex space-x-8">
          {navItems.map((item) => (
            <Link
              key={item.to}
              to={item.to}
              className={`flex items-center px-3 py-2 text-sm font-medium transition-colors duration-200 ${
                location.pathname === item.to
                  ? 'text-danier-dark bg-yellow-300'
                  : 'text-white hover:text-danier-dark hover:bg-yellow-200'
              }`}
            >
              {item.icon}
              <span className="ml-2">{item.label}</span>
            </Link>
          ))}
        </div>
      </div>
    </nav>
  );
}

export default App;
