import React, { useState, useEffect } from 'react';
import { Plus, Trash2, Mail, Users, Send, AlertCircle, CheckCircle } from 'lucide-react';
import { getRecipients, addRecipient, deleteRecipient, sendEmailAlert } from '../services/api';

const Recipients = () => {
  const [recipients, setRecipients] = useState([]);
  const [newEmail, setNewEmail] = useState('');
  const [newName, setNewName] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [sendingEmail, setSendingEmail] = useState(false);
  const [emailResult, setEmailResult] = useState(null);

  useEffect(() => {
    loadRecipients();
  }, []);

  const loadRecipients = async () => {
    try {
      setLoading(true);
      const response = await getRecipients();
      setRecipients(response.recipients || []);
    } catch (err) {
      setError('Failed to load recipients');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleAddRecipient = async (e) => {
    e.preventDefault();
    if (!newEmail.trim()) return;

    try {
      const response = await addRecipient(newEmail, newName);
      if (response.success) {
        setSuccess(response.message);
        setNewEmail('');
        setNewName('');
        loadRecipients();
      } else {
        setError(response.message);
      }
    } catch (err) {
      setError('Failed to add recipient');
      console.error(err);
    }
  };

  const handleDeleteRecipient = async (email) => {
    try {
      const response = await deleteRecipient(email);
      if (response.success) {
        setSuccess(response.message);
        loadRecipients();
      } else {
        setError(response.message);
      }
    } catch (err) {
      setError('Failed to delete recipient');
      console.error(err);
    }
  };

  const handleSendEmail = async () => {
    try {
      setSendingEmail(true);
      setEmailResult(null);

      const response = await sendEmailAlert();

      setEmailResult({
        success: response.success,
        message: response.message,
        email_sent: response.email_sent,
        recipients: response.recipients,
        items_count: response.items_count
      });

      if (response.success) {
        setSuccess(response.message);
      } else {
        setError(response.message);
      }
    } catch (err) {
      setError('Failed to send email');
      console.error(err);
    } finally {
      setSendingEmail(false);
    }
  };

  if (loading) {
    return (
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-danier-gold mx-auto"></div>
            <p className="mt-2 text-gray-600">Loading recipients...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="text-center mb-8">
          <h2 className="text-3xl font-bold text-danier-dark mb-2">
            Email Recipients
          </h2>
          <p className="text-gray-600">
            Manage who receives low stock alert emails
          </p>
        </div>

        {/* Add Recipient Form */}
        <div className="mb-8 p-6 bg-gray-50 rounded-lg">
          <h3 className="text-lg font-semibold text-danier-dark mb-4 flex items-center">
            <Plus className="w-5 h-5 mr-2" />
            Add New Recipient
          </h3>
          <form onSubmit={handleAddRecipient} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Email Address *
                </label>
                <input
                  type="email"
                  value={newEmail}
                  onChange={(e) => setNewEmail(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-danier-gold"
                  placeholder="recipient@example.com"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Name (Optional)
                </label>
                <input
                  type="text"
                  value={newName}
                  onChange={(e) => setNewName(e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-danier-gold"
                  placeholder="Recipient Name"
                />
              </div>
            </div>
            <button
              type="submit"
              className="bg-danier-gold text-white px-6 py-2 rounded-md hover:bg-yellow-600 flex items-center"
            >
              <Plus className="w-4 h-4 mr-2" />
              Add Recipient
            </button>
          </form>
        </div>

        {/* Current Recipients */}
        <div className="mb-8">
          <h3 className="text-lg font-semibold text-danier-dark mb-4 flex items-center">
            <Users className="w-5 h-5 mr-2" />
            Current Recipients ({recipients.length})
          </h3>
          <div className="space-y-3">
            {recipients.map((recipient, index) => (
              <div key={index} className="flex items-center justify-between p-4 bg-white border border-gray-200 rounded-lg">
                <div className="flex items-center">
                  <Mail className="w-5 h-5 text-blue-500 mr-3" />
                  <div>
                    <p className="font-medium text-gray-900">{recipient.email}</p>
                    <p className="text-sm text-gray-500">{recipient.name}</p>
                  </div>
                </div>
                <button
                  onClick={() => handleDeleteRecipient(recipient.email)}
                  className="text-red-600 hover:text-red-800 p-2"
                  title="Remove recipient"
                >
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            ))}
            {recipients.length === 0 && (
              <p className="text-gray-500 text-center py-4">No recipients added yet.</p>
            )}
          </div>
        </div>

        {/* Send Email Section */}
        <div className="mb-8 p-6 bg-blue-50 rounded-lg">
          <h3 className="text-lg font-semibold text-danier-dark mb-4 flex items-center">
            <Send className="w-5 h-5 mr-2" />
            Send Email Alert
          </h3>
          <p className="text-gray-600 mb-4">
            Send an immediate email alert to all recipients with current low stock information.
          </p>
          <button
            onClick={handleSendEmail}
            disabled={sendingEmail || recipients.length === 0}
            className="bg-blue-600 text-white px-6 py-3 rounded-md hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed flex items-center"
          >
            {sendingEmail ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                Sending...
              </>
            ) : (
              <>
                <Send className="w-4 h-4 mr-2" />
                Send Email Alert
              </>
            )}
          </button>
        </div>

        {/* Email Result */}
        {emailResult && (
          <div className={`p-4 rounded-lg mb-4 ${
            emailResult.success ? 'bg-green-50 border border-green-200' : 'bg-red-50 border border-red-200'
          }`}>
            <div className="flex items-center">
              {emailResult.success ? (
                <CheckCircle className="w-5 h-5 text-green-500 mr-2" />
              ) : (
                <AlertCircle className="w-5 h-5 text-red-500 mr-2" />
              )}
              <div>
                <p className={`font-medium ${emailResult.success ? 'text-green-800' : 'text-red-800'}`}>
                  {emailResult.message}
                </p>
                {emailResult.success && emailResult.email_sent && (
                  <p className="text-sm text-green-600 mt-1">
                    Sent to {emailResult.recipients?.length || 0} recipients with {emailResult.items_count || 0} low stock items
                  </p>
                )}
              </div>
            </div>
          </div>
        )}

        {/* Success/Error Messages */}
        {success && (
          <div className="mb-4 p-4 bg-green-50 border border-green-200 rounded-lg">
            <div className="flex items-center">
              <CheckCircle className="w-5 h-5 text-green-500 mr-2" />
              <p className="text-green-800">{success}</p>
            </div>
          </div>
        )}

        {error && (
          <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg">
            <div className="flex items-center">
              <AlertCircle className="w-5 h-5 text-red-500 mr-2" />
              <p className="text-red-800">{error}</p>
            </div>
          </div>
        )}

        {/* How it works */}
        <div className="mt-8 p-4 bg-gray-50 rounded-lg">
          <h4 className="font-semibold text-gray-900 mb-2">How it works:</h4>
          <ul className="text-sm text-gray-600 space-y-1">
            <li>• All recipients will receive email alerts when low stock items are detected</li>
            <li>• Emails are sent automatically after each inventory report upload</li>
            <li>• Recipients can be added or removed at any time</li>
            <li>• Email addresses must be valid and unique</li>
            <li>• Use the "Send Email Alert" button to manually send current alerts</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Recipients;
