import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from './ui/card';
import { Button } from './ui/button';
import { Badge } from './ui/badge';
import { Calendar, FileText, Package, AlertTriangle, TrendingUp, Database } from 'lucide-react';

const InventoryFilesDashboard = () => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileAlerts, setFileAlerts] = useState(null);
  const [loading, setLoading] = useState(true);
  const [allKeyItems, setAllKeyItems] = useState([]);

  useEffect(() => {
    fetchInventoryFiles();
  }, []);

  const fetchInventoryFiles = async () => {
    try {
      setLoading(true);
      const response = await fetch('/api/inventory-files');
      const data = await response.json();
      setFiles(data.files || []);
      setAllKeyItems(data.all_key_items || []);

      // Select the most recent file by default
      if (data.files && data.files.length > 0) {
        setSelectedFile(data.files[0]);
        fetchFileAlerts(data.files[0].filename);
      }
    } catch (error) {
      console.error('Error fetching inventory files:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchFileAlerts = async (filename) => {
    try {
      const response = await fetch(`/api/inventory-files/${filename}/alerts`);
      const data = await response.json();
      setFileAlerts(data);
    } catch (error) {
      console.error('Error fetching file alerts:', error);
    }
  };

  const handleFileSelect = (file) => {
    setSelectedFile(file);
    fetchFileAlerts(file.filename);
  };

  const formatDate = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleString();
  };

  const formatFileSize = (bytes) => {
    const mb = bytes / (1024 * 1024);
    return `${mb.toFixed(2)} MB`;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Inventory Files Dashboard</h2>
          <p className="text-gray-600">Multi-file KI00 tracking and analysis</p>
        </div>
        <div className="flex items-center space-x-2">
          <Database className="h-5 w-5 text-blue-600" />
          <span className="text-sm text-gray-600">
            {allKeyItems.length} total KI00 items detected
          </span>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Files List */}
        <div className="lg:col-span-1">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <FileText className="h-5 w-5" />
                <span>Inventory Files</span>
                <Badge variant="secondary">{files.length}</Badge>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {files.map((file) => (
                  <div
                    key={file.filename}
                    className={`p-3 rounded-lg border cursor-pointer transition-all ${
                      selectedFile?.filename === file.filename
                        ? 'border-blue-500 bg-blue-50'
                        : 'border-gray-200 hover:border-gray-300'
                    }`}
                    onClick={() => handleFileSelect(file)}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex-1">
                        <h4 className="font-medium text-sm text-gray-900 truncate">
                          {file.filename}
                        </h4>
                        <div className="flex items-center space-x-4 mt-1 text-xs text-gray-500">
                          <span className="flex items-center space-x-1">
                            <Calendar className="h-3 w-3" />
                            <span>{formatDate(file.upload_date)}</span>
                          </span>
                          <span>{formatFileSize(file.file_size)}</span>
                        </div>
                      </div>
                      <Badge variant="outline" className="ml-2">
                        {file.key_items_count} KI00
                      </Badge>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* File Details and Alerts */}
        <div className="lg:col-span-2">
          {selectedFile && fileAlerts ? (
            <div className="space-y-6">
              {/* File Summary */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Package className="h-5 w-5" />
                    <span>{selectedFile.filename}</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div className="text-center">
                      <div className="text-2xl font-bold text-blue-600">
                        {fileAlerts.key_items_tracked.length}
                      </div>
                      <div className="text-sm text-gray-600">KI00 Items</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-red-600">
                        {fileAlerts.low_stock_items.length}
                      </div>
                      <div className="text-sm text-gray-600">Low Stock</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-orange-600">
                        {fileAlerts.threshold}
                      </div>
                      <div className="text-sm text-gray-600">Threshold</div>
                    </div>
                    <div className="text-center">
                      <div className="text-2xl font-bold text-green-600">
                        {Object.keys(fileAlerts.summary).length}
                      </div>
                      <div className="text-sm text-gray-600">Categories</div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Key Items List */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <TrendingUp className="h-5 w-5" />
                    <span>KI00 Items in this File</span>
                    <Badge variant="secondary">{fileAlerts.key_items_tracked.length}</Badge>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-2">
                    {fileAlerts.key_items_tracked.map((item) => (
                      <Badge key={item} variant="outline" className="text-xs">
                        {item}
                      </Badge>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Low Stock Alerts */}
              {fileAlerts.low_stock_items.length > 0 && (
                <Card>
                  <CardHeader>
                    <CardTitle className="flex items-center space-x-2">
                      <AlertTriangle className="h-5 w-5 text-red-600" />
                      <span>Low Stock Alerts</span>
                      <Badge variant="destructive">{fileAlerts.low_stock_items.length}</Badge>
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-3 max-h-96 overflow-y-auto">
                      {fileAlerts.low_stock_items.slice(0, 20).map((item, index) => (
                        <div key={index} className="flex items-center justify-between p-3 bg-red-50 rounded-lg">
                          <div>
                            <div className="font-medium text-sm">{item.item_name}</div>
                            <div className="text-xs text-gray-600">
                              {item.color} - {item.size}
                            </div>
                          </div>
                          <div className="text-right">
                            <div className="text-sm font-medium text-red-600">
                              {item.current_stock} / {item.required_threshold}
                            </div>
                            <div className="text-xs text-gray-600">
                              Shortage: {item.shortage}
                            </div>
                          </div>
                        </div>
                      ))}
                      {fileAlerts.low_stock_items.length > 20 && (
                        <div className="text-center text-sm text-gray-500">
                          ... and {fileAlerts.low_stock_items.length - 20} more items
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              )}
            </div>
          ) : (
            <Card>
              <CardContent className="flex items-center justify-center h-64">
                <div className="text-center text-gray-500">
                  <FileText className="h-12 w-12 mx-auto mb-4" />
                  <p>Select a file to view details</p>
                </div>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
};

export default InventoryFilesDashboard;
