import React, { useState, useEffect } from 'react';
import { BarChart3, TrendingUp, AlertTriangle, Package, ChevronDown, ChevronRight, Mail, Users, Upload } from 'lucide-react';
import { getKeyItemsList, getSpecificKeyItemAlerts } from '../services/api';

const Dashboard = () => {
  const [keyItems, setKeyItems] = useState([]);
  const [expandedItems, setExpandedItems] = useState({});
  const [itemAlerts, setItemAlerts] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadKeyItems();
  }, []);

  const loadKeyItems = async () => {
    try {
      setLoading(true);
      const response = await getKeyItemsList();
      setKeyItems(response.key_items || []);
    } catch (err) {
      setError('Failed to load key items');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const toggleItem = async (itemName) => {
    if (expandedItems[itemName]) {
      // Collapse
      setExpandedItems(prev => ({ ...prev, [itemName]: false }));
    } else {
      // Expand and load alerts
      setExpandedItems(prev => ({ ...prev, [itemName]: true }));

      if (!itemAlerts[itemName]) {
        try {
          const response = await getSpecificKeyItemAlerts(itemName);
          setItemAlerts(prev => ({ ...prev, [itemName]: response.alerts || [] }));
        } catch (err) {
          console.error(`Failed to load alerts for ${itemName}:`, err);
          setItemAlerts(prev => ({ ...prev, [itemName]: [] }));
        }
      }
    }
  };

  const getStockStatus = (stock) => {
    if (stock <= 2) return { color: 'text-red-600', bg: 'bg-red-50', border: 'border-red-200' };
    if (stock <= 5) return { color: 'text-orange-600', bg: 'bg-orange-50', border: 'border-orange-200' };
    return { color: 'text-yellow-600', bg: 'bg-yellow-50', border: 'border-yellow-200' };
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-danier-gold mx-auto"></div>
            <p className="mt-2 text-gray-600">Loading key items dashboard...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-danier-dark mb-2">
            Danier Key Items Dashboard
          </h1>
          <p className="text-gray-600">
            Monitor stock levels for your key product styles
          </p>
        </div>
      </div>

      {/* Key Items Section */}
      <div className="bg-white rounded-lg shadow-md p-6">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold text-danier-dark flex items-center">
            <Package className="w-6 h-6 mr-3" />
            Key Items Stock Status
          </h2>
          <div className="text-sm text-gray-500">
            {keyItems.length} key styles tracked
          </div>
        </div>

        {error && (
          <div className="mb-4 p-4 bg-red-50 border border-red-200 rounded-lg">
            <p className="text-red-800">{error}</p>
          </div>
        )}

        {/* Key Items List */}
        <div className="space-y-4">
          {keyItems.map((itemName) => {
            const isExpanded = expandedItems[itemName];
            const alerts = itemAlerts[itemName] || [];
            const criticalCount = alerts.filter(alert => alert.current_stock <= 2).length;
            const lowCount = alerts.filter(alert => alert.current_stock > 2 && alert.current_stock <= 5).length;
            const warningCount = alerts.filter(alert => alert.current_stock > 5 && alert.current_stock < 10).length;

            return (
              <div key={itemName} className="border border-gray-200 rounded-lg overflow-hidden">
                {/* Item Header */}
                <div 
                  className="p-4 bg-gray-50 hover:bg-gray-100 cursor-pointer flex items-center justify-between"
                  onClick={() => toggleItem(itemName)}
                >
                  <div className="flex items-center">
                    {isExpanded ? (
                      <ChevronDown className="w-5 h-5 text-gray-500 mr-3" />
                    ) : (
                      <ChevronRight className="w-5 h-5 text-gray-500 mr-3" />
                    )}
                    <h3 className="text-lg font-semibold text-danier-dark">{itemName}</h3>
                  </div>

                  <div className="flex items-center space-x-4">
                    {/* Stock Status Indicators */}
                    {criticalCount > 0 && (
                      <span className="px-2 py-1 bg-red-100 text-red-800 text-xs font-medium rounded-full">
                        {criticalCount} Critical
                      </span>
                    )}
                    {lowCount > 0 && (
                      <span className="px-2 py-1 bg-orange-100 text-orange-800 text-xs font-medium rounded-full">
                        {lowCount} Low
                      </span>
                    )}
                    {warningCount > 0 && (
                      <span className="px-2 py-1 bg-yellow-100 text-yellow-800 text-xs font-medium rounded-full">
                        {warningCount} Warning
                      </span>
                    )}

                    <span className="text-sm text-gray-500">
                      {alerts.length} alerts
                    </span>
                  </div>
                </div>

                {/* Expanded Content */}
                {isExpanded && (
                  <div className="border-t border-gray-200">
                    {alerts.length > 0 ? (
                      <div className="p-4">
                        <h4 className="font-medium text-gray-900 mb-3">
                          Low Stock Items for {itemName}
                        </h4>
                        <div className="overflow-x-auto">
                          <table className="min-w-full divide-y divide-gray-200">
                            <thead className="bg-gray-50">
                              <tr>
                                <th className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                  Color
                                </th>
                                <th className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                  Size
                                </th>
                                <th className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                  Current Stock
                                </th>
                                <th className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                  Required
                                </th>
                                <th className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                  Shortage
                                </th>
                                <th className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                  Status
                                </th>
                              </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                              {alerts.map((alert, index) => {
                                const status = getStockStatus(alert.current_stock);
                                return (
                                  <tr key={index} className={`${status.bg} ${status.border}`}>
                                    <td className="px-3 py-2 whitespace-nowrap text-sm font-medium text-gray-900">
                                      {alert.color}
                                    </td>
                                    <td className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">
                                      {alert.size}
                                    </td>
                                    <td className={`px-3 py-2 whitespace-nowrap text-sm font-bold ${status.color}`}>
                                      {alert.current_stock}
                                    </td>
                                    <td className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">
                                      {alert.required_threshold}
                                    </td>
                                    <td className="px-3 py-2 whitespace-nowrap text-sm font-bold text-red-600">
                                      {alert.shortage}
                                    </td>
                                    <td className="px-3 py-2 whitespace-nowrap">
                                      {alert.current_stock <= 2 ? (
                                        <span className="px-2 py-1 text-xs font-medium bg-red-100 text-red-800 rounded-full">
                                          Critical
                                        </span>
                                      ) : alert.current_stock <= 5 ? (
                                        <span className="px-2 py-1 text-xs font-medium bg-orange-100 text-orange-800 rounded-full">
                                          Low
                                        </span>
                                      ) : (
                                        <span className="px-2 py-1 text-xs font-medium bg-yellow-100 text-yellow-800 rounded-full">
                                          Warning
                                        </span>
                                      )}
                                    </td>
                                  </tr>
                                );
                              })}
                            </tbody>
                          </table>
                        </div>
                      </div>
                    ) : (
                      <div className="p-4 text-center text-gray-500">
                        No low stock items found for {itemName}
                      </div>
                    )}
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {keyItems.length === 0 && (
          <div className="text-center py-8">
            <Package className="w-16 h-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-600 mb-2">No key items configured</h3>
            <p className="text-gray-500">Key items will appear here once configured</p>
          </div>
        )}
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6">
        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center">
            <Upload className="w-8 h-8 text-blue-600 mr-3" />
            <div>
              <h3 className="text-lg font-semibold text-gray-900">Upload Report</h3>
              <p className="text-gray-600">Upload latest inventory data</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center">
            <Mail className="w-8 h-8 text-green-600 mr-3" />
            <div>
              <h3 className="text-lg font-semibold text-gray-900">Email Alerts</h3>
              <p className="text-gray-600">Manage email recipients</p>
            </div>
          </div>
        </div>

        <div className="bg-white rounded-lg shadow-md p-6">
          <div className="flex items-center">
            <Users className="w-8 h-8 text-purple-600 mr-3" />
            <div>
              <h3 className="text-lg font-semibold text-gray-900">Recipients</h3>
              <p className="text-gray-600">Configure alert recipients</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
