import React, { useState, useEffect } from 'react';
import { Settings, Save, RotateCcw, AlertTriangle, Package, Ruler, Palette } from 'lucide-react';

const ThresholdManager = () => {
  const [customThresholds, setCustomThresholds] = useState({});
  const [defaultThreshold, setDefaultThreshold] = useState(10);
  const [loading, setLoading] = useState(false);
  const [newItemName, setNewItemName] = useState('');
  const [newSize, setNewSize] = useState('');
  const [newColor, setNewColor] = useState('');
  const [newThreshold, setNewThreshold] = useState('');
  const [message, setMessage] = useState('');
  const [availableOptions, setAvailableOptions] = useState({ colors: [], sizes: [] });

  // Common size options
  const sizeOptions = [
    '3XS', '2XS', 'XS', 'S', 'M', 'L', 'XL', '2XL', '3XL'
  ];

  // Common color options (matching inventory file format - uppercase)
  const colorOptions = [
    'BLACK', 'WHITE', 'BROWN', 'TAN', 'BEIGE', 'CREAM', 'GRAY', 'GREY', 'NAVY', 'BLUE', 'STEEL BLUE', 'DARK BLUE', 'RED', 'DARK RED', 'BORDEAUX', 'DARK BORDEAUX', 'GREEN', 'YELLOW', 'PINK', 'PURPLE', 'ORANGE', 'COGNAC', 'WHISKEY', 'CAMEL', 'MULTI', 'OTHER'
  ];

  useEffect(() => {
    loadThresholds();
  }, []);

  useEffect(() => {
    const fetchOptions = async () => {
      const name = newItemName.trim();
      if (!name) { setAvailableOptions({ colors: [], sizes: [] }); return; }
      try {
        const res = await fetch(`http://localhost:8000/key-items/options/${encodeURIComponent(name)}`);
        const data = await res.json();
        setAvailableOptions({
          colors: Array.isArray(data.colors) && data.colors.length ? data.colors : colorOptions,
          sizes: Array.isArray(data.sizes) && data.sizes.length ? data.sizes : sizeOptions,
        });
      } catch {
        setAvailableOptions({ colors: colorOptions, sizes: sizeOptions });
      }
    };
    fetchOptions();
  }, [newItemName]);

  const loadThresholds = async () => {
    setLoading(true);
    try {
      const response = await fetch('http://localhost:8000/thresholds/all');
      const data = await response.json();

      if (response.ok) {
        // Normalize to a map: "item|size|color" -> number
        if (data && typeof data.raw_thresholds === 'object' && data.raw_thresholds !== null) {
          setCustomThresholds(data.raw_thresholds);
        } else if (Array.isArray(data?.custom_thresholds)) {
          const normalized = {};
          for (const t of data.custom_thresholds) {
            const k = t.key || `${t.item_name}|${t.size}|${t.color}`;
            normalized[k] = typeof t.threshold === 'number' ? t.threshold : parseInt(t.threshold || '0', 10);
          }
          setCustomThresholds(normalized);
        } else if (data && typeof data.custom_thresholds === 'object' && data.custom_thresholds !== null) {
          // Already a map
          setCustomThresholds(data.custom_thresholds);
        } else {
          setCustomThresholds({});
        }
        setDefaultThreshold(data.default_threshold || 10);
      }
    } catch (error) {
      console.error('Failed to load thresholds:', error);
    } finally {
      setLoading(false);
    }
  };

  const setThreshold = async (itemName, size, color, threshold) => {
    try {
      // Create FormData for the API call
      const formData = new FormData();
      formData.append('item_name', itemName);
      formData.append('size', size);
      formData.append('color', color);
      formData.append('threshold', threshold.toString());

      const response = await fetch('http://localhost:8000/thresholds/set', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(`✅ Threshold set to ${threshold} for ${itemName} (${size}, ${color})`);
        loadThresholds(); // Reload to get updated data

        // Trigger dashboard refresh by dispatching a custom event
        window.dispatchEvent(new CustomEvent('thresholdsUpdated'));

        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage(`❌ Failed to set threshold: ${data.detail || 'Unknown error'}`);
        setTimeout(() => setMessage(''), 5000);
      }
    } catch (error) {
      console.error('Error setting threshold:', error);
      setMessage(`❌ Error setting threshold: ${error.message}`);
      setTimeout(() => setMessage(''), 5000);
    }
  };

  const resetThreshold = async (itemName, size, color) => {
    try {
      const response = await fetch(`http://localhost:8000/thresholds/reset/${encodeURIComponent(itemName)}/${encodeURIComponent(size)}/${encodeURIComponent(color)}`, {
        method: 'DELETE',
      });

      const data = await response.json();

      if (response.ok) {
        setMessage(`✅ Threshold reset to default for ${itemName} (${size}, ${color})`);
        loadThresholds(); // Reload to get updated data

        // Trigger dashboard refresh by dispatching a custom event
        window.dispatchEvent(new CustomEvent('thresholdsUpdated'));

        setTimeout(() => setMessage(''), 3000);
      } else {
        setMessage(`❌ Failed to reset threshold: ${data.detail || 'Unknown error'}`);
        setTimeout(() => setMessage(''), 5000);
      }
    } catch (error) {
      console.error('Error resetting threshold:', error);
      setMessage(`❌ Error resetting threshold: ${error.message}`);
      setTimeout(() => setMessage(''), 5000);
    }
  };

  const handleAddThreshold = () => {
    if (!newItemName.trim() || !newSize.trim() || !newColor.trim() || !newThreshold.trim()) {
      setMessage('❌ Please enter item name, size, color, and threshold');
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    const threshold = parseInt(newThreshold);
    if (isNaN(threshold) || threshold < 0) {
      setMessage('❌ Threshold must be a positive number');
      setTimeout(() => setMessage(''), 3000);
      return;
    }

    setThreshold(newItemName.trim(), newSize.trim(), newColor.trim(), threshold);
    setNewItemName('');
    setNewSize('');
    setNewColor('');
    setNewThreshold('');
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleAddThreshold();
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-6 mb-6">
      <h2 className="text-xl font-semibold mb-4 flex items-center">
        <Settings className="w-5 h-5 mr-2 text-blue-600" />
        Custom Stock Thresholds
      </h2>

      {message && (
        <div className={`mb-4 p-3 rounded-lg ${
          message.includes('✅') ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
        }`}>
          {message}
        </div>
      )}

      <div className="mb-6 p-4 bg-blue-50 rounded-lg">
        <h3 className="font-medium text-blue-900 mb-2">Default Threshold</h3>
        <p className="text-blue-700">
          All items use a default threshold of <strong>{defaultThreshold}</strong> units unless customized below.
        </p>
      </div>

      <div className="mb-6">
        <h3 className="font-medium text-gray-900 mb-3">Add Custom Threshold</h3>
        <div className="grid grid-cols-1 md:grid-cols-5 gap-2">
          <input
            type="text"
            value={newItemName}
            onChange={(e) => setNewItemName(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Item name (e.g., RONAN, JULIETTE)"
            className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
          <select
            value={newSize}
            onChange={(e) => setNewSize(e.target.value)}
            onKeyPress={handleKeyPress}
            className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white"
          >
            <option value="">Size</option>
            {(availableOptions.sizes.length ? availableOptions.sizes : sizeOptions).map(size => (
              <option key={size} value={size}>{size}</option>
            ))}
          </select>
          <select
            value={newColor}
            onChange={(e) => setNewColor(e.target.value)}
            onKeyPress={handleKeyPress}
            className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white"
          >
            <option value="">Color</option>
            {(availableOptions.colors.length ? availableOptions.colors : colorOptions).map(color => (
              <option key={color} value={color}>{color}</option>
            ))}
          </select>
          <input
            type="number"
            value={newThreshold}
            onChange={(e) => setNewThreshold(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Threshold"
            min="0"
            className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
          />
          <button
            onClick={handleAddThreshold}
            disabled={loading}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50 transition-colors flex items-center justify-center"
          >
            <Save className="w-4 h-4 mr-2" />
            Set
          </button>
        </div>
      </div>

      <div>
        <div className="flex items-center justify-between mb-4">
          <h3 className="font-medium text-gray-900">Custom Thresholds</h3>
          <button
            onClick={loadThresholds}
            disabled={loading}
            className="text-sm text-blue-600 hover:text-blue-800 disabled:opacity-50"
          >
            {loading ? 'Loading...' : 'Refresh'}
          </button>
        </div>

        {Object.keys(customThresholds).length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            <Settings className="w-12 h-12 mx-auto mb-4 text-gray-300" />
            <p>No custom thresholds set yet.</p>
            <p className="text-sm">Add custom thresholds above to override the default.</p>
          </div>
        ) : (
          <div className="space-y-3 max-h-96 overflow-y-auto">
            {Object.entries(customThresholds).map(([key, value]) => {
              const threshold = typeof value === 'number' ? value : parseInt(value || '0', 10);
              const [itemName, size, color] = key.split('|');
              return (
                <div key={key} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <h4 className="font-medium text-gray-900">{itemName}</h4>
                      <div className="flex items-center gap-4 mt-1 text-sm text-gray-600">
                        <span className="flex items-center">
                          <Ruler className="w-4 h-4 mr-1 text-blue-500" />
                          Size: {size}
                        </span>
                        <span className="flex items-center">
                          <Palette className="w-4 h-4 mr-1 text-purple-500" />
                          Color: {color}
                        </span>
                        <span className="flex items-center">
                          <AlertTriangle className="w-4 h-4 mr-1 text-orange-500" />
                          Custom: {threshold}
                        </span>
                        <span className="flex items-center">
                          <Package className="w-4 h-4 mr-1 text-gray-500" />
                          Default: {defaultThreshold}
                        </span>
                      </div>
                    </div>
                    <div className="flex items-center gap-2">
                      <input
                        type="number"
                        defaultValue={threshold}
                        min="0"
                        className="w-20 px-2 py-1 border border-gray-300 rounded text-sm"
                        onBlur={(e) => {
                          const newValue = parseInt(e.target.value);
                          if (!isNaN(newValue) && newValue >= 0 && newValue !== threshold) {
                            setThreshold(itemName, size, color, newValue);
                          }
                        }}
                        onKeyPress={(e) => {
                          if (e.key === 'Enter') {
                            e.target.blur();
                          }
                        }}
                      />
                      <button
                        onClick={() => resetThreshold(itemName, size, color)}
                        className="p-2 text-gray-500 hover:text-red-600 transition-colors"
                        title="Reset to default"
                      >
                        <RotateCcw className="w-4 h-4" />
                      </button>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default ThresholdManager;
